import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom/client";
import Todo_list from "./Todo";
import Coin_Tracker from "./Coin";
import Movie from "./movie";

const root = ReactDOM.createRoot(document.getElementById("root"));
function APP() {
  const [page, setPage] = useState("");
  const [choosen, setChooesn] = useState(false);
  const pageChange = (event) => {
    setPage(event.target.value);
  };
  useEffect(() => {
    setChooesn((prev) => !prev);
  }, [page]);
  return (
    <div>
      {choosen && page === "todo" ? (
        <div>
          <h1>Choose your Page</h1>
          <select value={page} onChange={pageChange}>
            <option>select your page</option>
            <option>todo</option>
            <option>coin</option>
            <option>movie</option>
          </select>
        </div>
      ) : null}
    </div>
  );
}
root.render(<APP />);

