import Btn from "./button.js";
import styles from "./App.module.css";
import { useState, useEffect } from "react";

function App() {
  const [counter, setCount] = useState(0);
  const [val, setVal] = useState("");
  const [show, setShow] = useState(false);
  const onClick = () => {
    setCount((current) => current + 1);
  };
  const clickShow = () => {
    setShow((prev) => !prev);
  };
  const onChange = (event) => {
    setVal(event.target.value);
  };
  console.log("reder");
  useEffect(() => {
    console.log("once");
  }, []);
  useEffect(() => {
    if (val != "") {
      console.log("Search for", val);
    }
  }, [val]);
  return (
    <div>
      <input value={val} onChange={onChange} type="text" placeholder="Search" />
      {show ? <Title text={counter} /> : null}
      <Btn text={"count!"} onClick={onClick} />
      <Btn text={show ? "Hide" : "show"} onClick={clickShow} />
    </div>
  );
}

export default App;
